$(document).ready(function () {

    var TNC = {
        eles: {
            parent: '.jquery-app-chat',
            title: '.jquery-app-chat-title',
            tabs: {
                list_friends: '.jquery-app-chat-list-friends'
            },
            btn: {
                toggle_lfs: '.jquery-app-btn-toggle-lfs'
            }
        },
        init: function () {
            TNC.registerEvents();
        },
        registerEvents: function () {
            $(TNC.eles.btn.toggle_lfs).on('click', function () {
                $(TNC.eles.tabs.list_friends).slideToggle();
            });
            $(TNC.eles.title).on('click', function () {
                $(TNC.eles.tabs.list_friends).slideToggle();
            });
            
        }
    };

    TNC.init();

});